import { useSelector } from 'react-redux'
import Contato from '../../Contato'
import { MainContainer, Titulo } from '../../styles'
import { RootState } from '../../store'

const ListaDeContatos = () => {
  // Seleciona os contatos e o termo do filtro no Redux
  const { itens, termo } = useSelector((state: RootState) => state.contatos)

  // Filtra contatos pelo termo (nome ou e-mail)
  const filtraContatos = () => {
    if (!termo || termo.trim() === '') {
      return itens
    }
    const termoMinusculo = termo.toLowerCase()
    return itens.filter(
      (contato) =>
        contato.nome.toLowerCase().includes(termoMinusculo) ||
        contato.email.toLowerCase().includes(termoMinusculo)
    )
  }

  const contatosFiltrados = filtraContatos()

  const mensagemResultado =
    contatosFiltrados.length === 0
      ? 'Nenhum contato encontrado'
      : `${contatosFiltrados.length} contato(s) encontrado(s)`

  return (
    <MainContainer>
      <Titulo as="p">{mensagemResultado}</Titulo>
      <ul>
        {contatosFiltrados.map((contato) => (
          <li key={contato.id}>
            <Contato
              id={contato.id}
              nome={contato.nome}
              email={contato.email}
              telefone={contato.telefone}
            />
          </li>
        ))}
      </ul>
    </MainContainer>
  )
}

export default ListaDeContatos
