import styled from 'styled-components'

export const Card = styled.div`
  background-color: ${({ theme }) => theme.cores.secundaria};
  border-radius: 12px;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.05);
  padding: 24px;
  transition: transform 0.3s;
  display: flex;
  flex-direction: column;
  gap: 12px;

  &:hover {
    transform: translateY(-4px);
  }
`

export const Titulo = styled.h3`
  font-size: 1.3rem;
  font-weight: 600;
  color: ${({ theme }) => theme.cores.primaria};
`

export const Descricao = styled.input`
  border: 1px solid ${({ theme }) => theme.cores.borda};
  border-radius: 6px;
  padding: 8px 12px;
  font-size: 1rem;
  background-color: ${({ disabled, theme }) =>
    disabled ? '#f1f1f1' : theme.cores.fundo};
  transition: all 0.2s;
  &:focus {
    outline: none;
    border-color: ${({ theme }) => theme.cores.primaria};
  }
`

export const BarraAcoes = styled.div`
  display: flex;
  gap: 8px;
  margin-top: 8px;
  flex-wrap: wrap;
`

const BotaoBase = styled.button`
  border: none;
  border-radius: 6px;
  padding: 8px 12px;
  font-size: 0.9rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 6px;
  transition: background-color 0.2s, transform 0.2s;

  &:hover {
    transform: translateY(-2px);
  }
`

export const BotaoSalvar = styled(BotaoBase)`
  background-color: #4caf50;
  color: white;
`

export const BotaoEditar = styled(BotaoBase)`
  background-color: #1976d2;
  color: white;
`

export const BotaoCancelarRemover = styled(BotaoBase)`
  background-color: #e53935;
  color: white;
`
