import { useDispatch, useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import * as S from './styles'
import { alterarTermo } from '../../store/reducers/filtro' // novo reducer para filtro contatos
import { RootState } from '../../store'
import { Botao, Campo } from '../../styles'

type Props = {
  mostrarFiltros: boolean
}

const BarraLateral = ({ mostrarFiltros }: Props) => {
  const navigate = useNavigate()
  const dispatch = useDispatch()
  const { termo } = useSelector((state: RootState) => state.filtro)

  return (
    <S.Aside>
      <div>
        {mostrarFiltros ? (
          <>
            <Campo
              type="text"
              placeholder="Buscar contatos por nome ou e-mail"
              value={termo}
              onChange={(e) => dispatch(alterarTermo(e.target.value))}
            />
            {/* Aqui pode colocar mais filtros específicos para contatos, se desejar */}
          </>
        ) : (
          <Botao onClick={() => navigate('/')}>Voltar à lista de contatos</Botao>
        )}
      </div>
    </S.Aside>
  )
}

export default BarraLateral
