import { createSlice, PayloadAction } from '@reduxjs/toolkit'
import Contato from '../../models/Contato'

type ContatosState = {
  itens: Contato[]
  termo: string  // adiciona termo para busca/filtro
}

const initialState: ContatosState = {
  itens: [
    {
      id: 1,
      nome: 'Débora Sete Acosta',
      email: 'deborasete@gmail.com',
      telefone: '(11) 99999-0000'
    },
    {
      id: 2,
      nome: 'Maria Consuelo',
      email: 'mariaconsuelo@gmail.com',
      telefone: '(11) 98888-0000'
    },
    {
      id: 3,
      nome: 'João Gael',
      email: 'joaogael@gmail.com',
      telefone: '(11) 97777-0000'
    }
  ],
  termo: ''
}

const contatosSlice = createSlice({
  name: 'contatos',
  initialState,
  reducers: {
    remover: (state, action: PayloadAction<number>) => {
      state.itens = state.itens.filter((c) => c.id !== action.payload)
    },
    editar: (state, action: PayloadAction<Contato>) => {
      const index = state.itens.findIndex((c) => c.id === action.payload.id)
      if (index >= 0) {
        state.itens[index] = action.payload
      }
    },
    cadastrar: (state, action: PayloadAction<Omit<Contato, 'id'>>) => {
      const contatoExistente = state.itens.find(
        (c) => c.email.toLowerCase() === action.payload.email.toLowerCase()
      )

      if (contatoExistente) {
        alert('Já existe um contato com esse e-mail')
      } else {
        const ultimo = state.itens[state.itens.length - 1]
        const novoContato = {
          ...action.payload,
          id: ultimo ? ultimo.id + 1 : 1
        }
        state.itens.push(novoContato)
      }
    },
    alterarTermo: (state, action: PayloadAction<string>) => {
      state.termo = action.payload
    }
  }
})

export const { remover, editar, cadastrar, alterarTermo } = contatosSlice.actions
export default contatosSlice.reducer
